mutation LoginUser($username: String!, $password: String!) {
  login(loginUserInput: {username: $username, password: $password}) {
    token
    __typename
  }
}

query {
  applications(
    options: {
      limit: 5
      offset: 0
      sortBy: "createdOn"
      sortOrder: ASC
      search: "Pinestem"
      filters: [{ field: "applicationId", operator: "eq", value: "1" }]
    }
  ) {
    applications {
        applicationId
        name
        userId
        createdOn
        updatedOn
        status
    }
    total,
    offset,
    limit
  }
}

mutation CreateApplication {
    application(createApplicationInput: {
        name: "<newApplicationName>",
    }) {
        applicationId
        name
        userId
        createdOn
        updatedOn
        status
    }
}

query {
  providers(
    options: {
      limit: 5
      offset: 0
      sortBy: "createdOn"
      sortOrder: ASC
    }
  ) {
    providers {
        providerId
        name
        channelType
        configuration
        isEnabled
        userId
        createdOn
        updatedOn
        status
    }
    total,
    offset,
    limit
  }
}

mutation RegisterWebhook {
    webhook(createWebhookInput: {
        providerId: 10,
        webhookUrl: "http://localhost:4200/webhook",
    }) {
        webhookUrl
        providerId
        createdOn
        updatedOn
        status
    }
}
