mutation LoginUser($username: String!, $password: String!) {
  login(loginUserInput: {username: $username, password: $password}) {
    token
    __typename
  }
}

query {
  applications(
    options: {
      limit: 5
      offset: 0
      sortBy: "createdOn"
      sortOrder: ASC
    #   search: "Pinestem"
      filters: [{ field: "applicationId", operator: "eq", value: "1" }]
    }
  ) {
    applications {
        applicationId
        name
        userId
        testModeEnabled
        whitelistRecipients
        createdOn
        updatedOn
        status
    }
    total,
    offset,
    limit
  }
}

mutation CreateApplication
($whitelistRecipients: JSONObject!) 
{
    application(createApplicationInput: {
        name: "<newApplicationName>",
        testModeEnabled: 0,
        whitelistRecipients: $whitelistRecipients,
    }) {
        applicationId
        name
        userId
        testModeEnabled
        whitelistRecipients
        createdOn
        updatedOn
        status
    }
}

mutation UpdateApplication($applicationId: Float!, $whitelistRecipients: JSONObject!) {
    updateApplication(updateApplicationInput: {
        applicationId: $applicationId,
        name: "<updatedApplicationName>",
        testModeEnabled: 1,
        whitelistRecipients: $whitelistRecipients,
    }) {
        applicationId
        name
        userId
        testModeEnabled
        whitelistRecipients
        createdOn
        updatedOn
        status
    }
}

